import React, { Component } from 'react';
import './App.css';
import { CardList } from './components/card-list/card-list.component';

class App extends Component {
  constructor(){
    super();

    this.state = {
      houses: [],
      searchField: ''
    };
  }

  componentDidMount(){
    fetch('https://my-json-server.typicode.com/karenhernandeze/Casas/db')
    .then(response => response.json())
    .then(data => this.setState({houses: data.housesdb})); //console.log(users));
  }

  render() {
    const { monsters, searchField } = this.state;
//    const fm = monsters.filter
    const filteredMonsters = monsters.filter(monster => 
      monster.name.toLowerCase().includes(searchField.toLowerCase())
    )
    return (
      <div className="App">
      <input type='search' placeholder='search house' 
      onChange={e => {
        this.setState({searchField: e.target.value})}}></input>
      <CardList houses={filteredMonsters}/> 
      </div>
    )
  }
}

export default App;
